INSERT INTO seqtext VALUES(150,64,3,'lilly',367,240,10);
INSERT INTO seqtext VALUES(150,64,3,'akira',435,560,10);
INSERT INTO seqtext VALUES(150,64,1,'Posant doucement ma main sur le ct de la tasse, je me rends compte qu''il est dj  la bonne temprature pour le boire. Aprs avoir pris une gorge, le got se rvle tre aussi bon que la temprature.',1,0,0);
INSERT INTO seqtext VALUES(150,65,1,'Yuuko semble tre bien meilleure  a qu''au rle de bibliothcaire.',1,0,0);
