INSERT INTO seqtext VALUES(161,13,1,'Les rafales du carillon et la voix de fausset du principal annoncent l''ouverture du festival via les haut-parleurs.',1,0,0);
INSERT INTO seqtext VALUES(161,14,1,'Tout le monde applaudit poliment avec un peu d''enthousiasme.',1,0,0);
INSERT INTO seqtext VALUES(161,15,1,'Le festival de l''cole... on n''avait pas vraiment de festivals dans mon ancienne cole. a fait un peu vieux jeu, surtout compte tenu de l''cole d''o je viens, mais c''est quand mme un peu excitant.',1,0,0);
INSERT INTO seqtext VALUES(161,16,1,'Un jour de repos est agrable aprs cette premire semaine de dur travail, malgr le fait que je sois rest allong dans un lit d''hpital pendant quatre mois avant a.',1,0,0);
INSERT INTO seqtext VALUES(161,17,1,'Je me rappelle mme avoir souhait pouvoir aller  des cours de maths durant mon sjour  l''hpital.',1,0,0);
INSERT INTO seqtext VALUES(161,18,1,'Je ne me souviens pas du programme du festival, mme si Mutou l''a parcouru pendant la classe l''autre jour.',1,0,0);
INSERT INTO seqtext VALUES(161,19,1,'Je descends les marches du dortoir, avec l''intention de faire le tour de la cour pour voir tout ce qui est mis en place, mais je reste en bas des escaliers.',1,0,0);
INSERT INTO seqtext VALUES(161,20,4,'crowd',0,0,0);
INSERT INTO seqtext VALUES(161,21,3,'rin',273,560,10);
INSERT INTO seqtext VALUES(161,21,1,'Quelques personnes contemplent la peinture de Rin, tandis que l''artiste elle-mme se prlasse sur le ct, appuye contre le mur et ayant l''air extrmement ennuye.',1,0,0);
