INSERT INTO seqtext VALUES(161,26,1,'Je suis coince.',15,0,0);
INSERT INTO seqtext VALUES(161,27,1,'Comment a coince ?',12,0,0);
INSERT INTO seqtext VALUES(161,28,1,'Je veux dire que je ne peux pas marcher. Je crois que mes jambes sont hors service  cause d''hier.',15,0,0);
INSERT INTO seqtext VALUES(161,29,1,'a te fait mal ?',12,0,0);
INSERT INTO seqtext VALUES(161,30,3,'rin',180,560,10);
INSERT INTO seqtext VALUES(161,30,1,'Dur  dire. Peut-tre.',15,0,0);
INSERT INTO seqtext VALUES(161,31,1,'L''tendue du travail qu''elle a fait tait plus importante qu''elle ne me l''a laiss savoir. Je pensais qu''elle avait juste un peu les muscles fatigus ou un truc du genre. Je veux poser une question, mais Rin change rapidement de sujet.',1,0,0);
INSERT INTO seqtext VALUES(161,32,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(161,32,1,'Les amis du prof sont passs.',15,0,0);
INSERT INTO seqtext VALUES(161,33,3,'rin',588,560,10);
INSERT INTO seqtext VALUES(161,33,1,'Et ils sont alls en ville pour djeuner et m''ont demand d''aller avec eux. C''tait une bonne chose, mes jambes me faisaient vraiment mal.',15,0,0);
INSERT INTO seqtext VALUES(161,34,1,'Mais t''es bloque, assise ici ? C''est dommage.',12,0,0);
INSERT INTO seqtext VALUES(161,35,3,'rin',96,560,10);
INSERT INTO seqtext VALUES(161,35,1,'Je vais juste attendre jusqu'' ce que je puisse remarcher  nouveau. a va arriver tt ou tard, quand on y pense.',15,0,0);
INSERT INTO seqtext VALUES(161,36,1,'Le prof tait content que j''aie fini le mur.',15,0,0);
