INSERT INTO seqtext VALUES(161,39,1,'Oh ?',12,0,0);
INSERT INTO seqtext VALUES(161,40,3,'rin',546,560,10);
INSERT INTO seqtext VALUES(161,40,1,'Je croyais hier que j''avais tout fini, mais maintenant je n''en suis plus aussi sre. Je devrais peindre plein de dtails. Peut-tre. Probablement. C''est trs dur de dcider.',15,0,0);
INSERT INTO seqtext VALUES(161,41,1,'Finie ou non, la peinture murale est vraiment bien au grand jour.',1,0,0);
INSERT INTO seqtext VALUES(161,42,2,'',432,0,0);
INSERT INTO seqtext VALUES(161,43,1,'Diverses parties du corps humain, rptes encore et encore avec une mutilation sauvage, pour la plupart dfigures, en sont les lments principaux.',1,0,0);
INSERT INTO seqtext VALUES(161,44,1,'a a l''air anarchique, comme si c''tait inconsidrment plac et rudimentairement peint, mais beaucoup de rflexion et de soin ont t consacrs  chacune d''entre elles.',1,0,0);
INSERT INTO seqtext VALUES(161,45,3,'bg',432,400,10);
