INSERT INTO seqtext VALUES(161,89,1,'Je suis bien comme a.',15,0,0);
INSERT INTO seqtext VALUES(161,90,1,'Tu ne socialises pas beaucoup, hein ?',12,0,0);
INSERT INTO seqtext VALUES(161,91,1,'Je crois que je demande plus pour elle que pour moi  ce point. C''est pas comme si j''tais particulirement excit  propos du festival, non plus; juste un peu curieux de voir de quoi a a l''air, et c''est tout.',1,0,0);
INSERT INTO seqtext VALUES(161,92,1,'Sa rponse est surprenante.',1,0,0);
INSERT INTO seqtext VALUES(161,93,3,'rin',226,560,10);
INSERT INTO seqtext VALUES(161,93,1,'Non, pas trop.',15,0,0);
