INSERT INTO seqtext VALUES(161,95,3,'rin',104,560,10);
INSERT INTO seqtext VALUES(161,95,1,'Tu devrais y aller si t''en as envie.',15,0,0);
INSERT INTO seqtext VALUES(161,96,1,'Je sais, mais je peux te tenir compagnie. Je ne suis pas encore habitu  tout a, donc a me va si je reste au calme.',12,0,0);
