INSERT INTO seqtext VALUES(161,123,3,'rin',420,560,10);
INSERT INTO seqtext VALUES(161,123,1,'D''habitude j''aime les intermdiaires, mais l, a m''a fait mal aux fesses. Pas littralement, bien sr... et puis j''ai eu a. Je suppose que c''tait littral aprs tout.',15,0,0);
