INSERT INTO seqtext VALUES(161,125,3,'rin',214,560,10);
INSERT INTO seqtext VALUES(161,125,1,'Elle me regarde d''un air amus, mais ne dit rien.',1,0,0);
INSERT INTO seqtext VALUES(161,126,2,'',85,0,0);
INSERT INTO seqtext VALUES(161,127,1,' ce moment je commence  me demander si je ne devrais pas vraiment partir et faire quelque chose de plus constructif de mon dimanche.',1,0,0);
INSERT INTO seqtext VALUES(161,128,1,'C''est le summum de l''chec social. Une journe entire de libre, un festival juste derrire ma porte, et qu''est-ce que je fais ?',1,0,0);
