INSERT INTO seqtext VALUES(161,142,3,'rin',546,400,10);
INSERT INTO seqtext VALUES(161,142,1,'Elle bondit soudainement sur ses pieds. C''est surprenant, je croyais qu''elle en tait toujours incapable, mais apparemment ce n''est pas le cas.',1,0,0);
