INSERT INTO seqtext VALUES(161,148,3,'rin',449,560,10);
INSERT INTO seqtext VALUES(161,148,1,'Les lvres de Rin s''aplatissent l''une contre l''autre pour former une ligne presque parfaitement horizontale.',1,0,0);
INSERT INTO seqtext VALUES(161,149,3,'rin',96,560,10);
INSERT INTO seqtext VALUES(161,149,1,'Elle ferme les yeux et prend une profonde respiration.',1,0,0);
INSERT INTO seqtext VALUES(161,150,1,'Quand elle rouvre ses paupires et laisse apparatre ses yeux noirs d''un aspect terriblement srieux, a me prend au dpourvu.',1,0,0);
INSERT INTO seqtext VALUES(161,151,3,'rin',102,560,10);
INSERT INTO seqtext VALUES(161,151,1,'Hisao, tu pourrais ne pas vouloir entendre a ou peut-tre que si, je ne sais pas, mais a ne compte pas et mme si c''est le cas tu ne me laisses pas le choix.',15,0,0);
INSERT INTO seqtext VALUES(161,152,1,'J''ai mes rgles et j''ai besoin de l''aide de quelqu''un. Cependant, je ne crois pas que notre relation soit arrive au niveau o je peux t''autoriser  retirer mes sous-vtements dans les toilettes des filles, mme si tu me le proposais.',15,0,0);
INSERT INTO seqtext VALUES(161,153,1,'C''est pourquoi tu devrais rester ici pendant que je vais chercher Emi.',15,0,0);
INSERT INTO seqtext VALUES(161,154,1,'Pendant que le sang me monte aux joues telle la mare, mon cerveau essaye dsesprment de trouver une rponse mais la seule chose  laquelle je pense est le fait que c''est la chose la plus cohrente que Rin a pu dire depuis ces quatre derniers jours.',1,0,0);
INSERT INTO seqtext VALUES(161,155,1,'Oui.',12,0,0);
INSERT INTO seqtext VALUES(161,156,4,'rin',0,0,0);
INSERT INTO seqtext VALUES(161,157,1,'Ne voulant pas croiser Rin du regard, je tourne la tte, prtendant regarder les parents de quelqu''un.',1,0,0);
