INSERT INTO seqtext VALUES(163,84,1,'Elles hochent toutes deux la tte avant qu''on sorte de la bibliothque pour se diriger vers les festivits.',1,0,0);
INSERT INTO seqtext VALUES(163,85,2,'',390,0,0);
INSERT INTO seqtext VALUES(163,86,1,'J''ai un bref change avec la fille derrire le comptoir, et prends deux gobelets de th. J''essaye d''accentuer ma salutation en me penchant un peu plus pour compenser le fait qu''elle est de toute vidence sourde.',1,0,0);
