INSERT INTO seqtext VALUES(163,90,3,'lilly',244,240,10);
INSERT INTO seqtext VALUES(163,90,3,'hanako',84,560,10);
INSERT INTO seqtext VALUES(163,90,1,'C''est un endroit vraiment agrable. clair seulement par la lune, il est un peu loign des vnements principaux.',1,0,0);
INSERT INTO seqtext VALUES(163,91,1,'Compar  la chaleur qu''il a fait pendant la journe, cet endroit est agrablement frais maintenant.',1,0,0);
INSERT INTO seqtext VALUES(163,92,1,'Pas que a importe tant que a. La plupart des visiteurs sont partis aux zones qui sont soit plus proches des feux d''artifice, ou plus hautes sur la colline pour voir le spectacle.',1,0,0);
INSERT INTO seqtext VALUES(163,93,3,'lilly',564,240,10);
INSERT INTO seqtext VALUES(163,93,1,'Oh, tu es revenu, Hisao.',16,0,0);
INSERT INTO seqtext VALUES(163,94,3,'hanako',425,560,10);
INSERT INTO seqtext VALUES(163,94,1,'Ses oreilles sont bonnes. Je ne suis mme pas  proximit et Hanako ne m''avait pas remarqu.',1,0,0);
INSERT INTO seqtext VALUES(163,95,1,'Voil. Dsol c''est du th instantan, mais c''est tout ce qu''il leur restait.',12,0,0);
INSERT INTO seqtext VALUES(163,96,1,'Hanako prend dlicatement sa tasse de la main droite, et je place doucement l''autre dans les mains tendues de Lilly.',1,0,0);
INSERT INTO seqtext VALUES(163,97,3,'hanako',589,560,10);
INSERT INTO seqtext VALUES(163,97,3,'lilly',244,240,10);
INSERT INTO seqtext VALUES(163,97,1,'Tu as apprci le festival alors, Hisao ?',16,0,0);
INSERT INTO seqtext VALUES(163,98,1,'Ouais, c''est plutt fun.',12,0,0);
INSERT INTO seqtext VALUES(163,99,1,'Une rponse honnte. La nourriture n''tait en gnral pas super, mais je me suis bien amus en fin de compte, spcialement avec Hanako et Lilly.',1,0,0);
INSERT INTO seqtext VALUES(163,100,1,'En fait, je crois qu''elles ont encore plus apprci que moi. Avec Lilly et moi  ct, Hanako a russi  tre suffisamment  l''aise pour pouvoir apprcier le festival.',1,0,0);
INSERT INTO seqtext VALUES(163,101,2,'',409,0,0);
INSERT INTO seqtext VALUES(163,102,1,'Pendant qu''on est assis  boire, les sifflements des premiers feux d''artifice se font entendre avant qu''ils explosent dans une douche vibrante de bleu dans le ciel de la nuit, annonant le dbut de la fin pour le festival.',1,0,0);
INSERT INTO seqtext VALUES(163,103,1,'Des cris enthousiastes peuvent tre entendus dans la foule disperse sur le terrain de l''cole pour clbrer a.',1,0,0);
INSERT INTO seqtext VALUES(163,104,1,'Pendant quelques minutes sur la fin, Hanako et moi regardons les feux d''artifice alors que Lilly coute batement avec les yeux ferms.',1,0,0);
INSERT INTO seqtext VALUES(163,105,1,'Rouge, vert, jaune, en forme d''toile, de rond ou de figures, toutes les formes et toutes les couleurs remplissent l''air, une aprs l''autre, et dansent dans le ciel.',1,0,0);
INSERT INTO seqtext VALUES(163,106,1,'Aucun endroit prs d''o j''ai vcu n''a jamais eu d''exhibition aussi grandiose. Les festivals comme a sont une chose qui appartient au pass, dans une zone aussi mtropolitaine.',1,0,0);
INSERT INTO seqtext VALUES(163,107,1,'Voir un tel spectacle avec elles deux... c''est probablement le moment le plus agrable que j''ai eu depuis longtemps.',1,0,0);
INSERT INTO seqtext VALUES(163,108,2,'',11,0,0);
INSERT INTO seqtext VALUES(163,109,3,'lilly',218,240,10);
INSERT INTO seqtext VALUES(163,109,3,'hanako',84,560,10);
INSERT INTO seqtext VALUES(163,109,1,'Donc... voil. Le festival est finalement termin.',16,0,0);
INSERT INTO seqtext VALUES(163,110,1,'Ouais.',12,0,0);
INSERT INTO seqtext VALUES(163,111,1,'Elle laisse chapper un dlicat soupir nostalgique.',1,0,0);
INSERT INTO seqtext VALUES(163,112,3,'lilly',150,240,10);
INSERT INTO seqtext VALUES(163,112,1,'Autant je pourrais me plaindre  propos de tout ce qu''on a d faire, mais je suis toujours triste de constater que je serai diplme avant le prochain festival de Yamaku.',16,0,0);
INSERT INTO seqtext VALUES(163,113,3,'lilly',559,240,10);
INSERT INTO seqtext VALUES(163,113,1,'Je m''avance et me tiens  ct de Lilly, posant doucement une main sur son paule.',1,0,0);
INSERT INTO seqtext VALUES(163,114,1,'T''inquite pas. On a toujours Tanabata plus tard dans l''anne, hein ?',12,0,0);
INSERT INTO seqtext VALUES(163,115,3,'lilly',252,240,10);
INSERT INTO seqtext VALUES(163,115,1,'Tu as raison. a serait bien d''y aller quand a arrivera.',16,0,0);
INSERT INTO seqtext VALUES(163,116,1,'Plusieurs minutes passent en silence, avec seulement les explosions des feux d''artifice pouvant tre entendues.',1,0,0);
INSERT INTO seqtext VALUES(163,117,1,'Les voir toutes les deux me rappelle le conseil que m''avait donn Lilly quand on avait t en ville ensemble.',1,0,0);
INSERT INTO seqtext VALUES(163,118,1,'Chrir l''opportunit de se faire de nouveaux amis, hein ?',1,0,0);
INSERT INTO seqtext VALUES(163,119,1,'Dis, Lilly ?',12,0,0);
INSERT INTO seqtext VALUES(163,120,3,'lilly',115,240,10);
INSERT INTO seqtext VALUES(163,120,1,'Elle tourne lgrement la tte vers moi pour me montrer qu''elle coute, le regard d''Hanako est encore fix sur les explosions d''artifice multicolores.',1,0,0);
INSERT INTO seqtext VALUES(163,121,1,'a te gne si je vous rejoins de temps en temps toutes les deux pour boire le th?',12,0,0);
INSERT INTO seqtext VALUES(163,122,1,'Le sourire sur son visage est plus que suffisant pour voir sa rponse.',1,0,0);
INSERT INTO seqtext VALUES(163,123,3,'lilly',32,240,10);
INSERT INTO seqtext VALUES(163,123,1,'a serait un plaisir, Hisao.',16,0,0);
INSERT INTO seqtext VALUES(164,1,2,'',456,0,0);
INSERT INTO seqtext VALUES(164,2,3,'lilly',332,400,10);
INSERT INTO seqtext VALUES(164,2,1,'Lilly ne semble pas rassure du tout, et je ne peux pas l''en blmer.',1,0,0);
INSERT INTO seqtext VALUES(164,3,1,'En plus de ses problmes avec son stand, elle semble toujours s''inquiter pour Hanako.',1,0,0);
INSERT INTO seqtext VALUES(164,4,1,'Je ne peux pas vraiment l''aider avec sa classe, donc je suppose que la seule faon de lui venir en aide est d''essayer de la rassurer pour notre amie timide en commun.',1,0,0);
INSERT INTO seqtext VALUES(164,5,1,'Plaant le bol sur le comptoir, j''appelle Lilly.',1,0,0);
INSERT INTO seqtext VALUES(164,6,1,'H, Lilly, ne t''inquite pas pour Hanako, je vais la trouver et rester avec elle, d''accord?',12,0,0);
INSERT INTO seqtext VALUES(164,7,3,'lilly',50,400,10);
INSERT INTO seqtext VALUES(164,7,1,'Je peux voir l''apaisement de Lilly clairement crit sur son visage.',1,0,0);
INSERT INTO seqtext VALUES(164,8,1,'Merci Hisao. Et si tu vois les lves de ma classe, tu peux leur dire de revenir ici s''il te plat ?',16,0,0);
INSERT INTO seqtext VALUES(164,9,1,'Je le ferai. Amuse-toi bien. Et sois sre de prendre une pause, hein ?',12,0,0);
INSERT INTO seqtext VALUES(164,10,3,'lilly',367,400,10);
INSERT INTO seqtext VALUES(164,10,1,'Je le ferai si je le peux.  plus tard, Hisao.',16,0,0);
INSERT INTO seqtext VALUES(164,11,2,'',186,0,0);
INSERT INTO seqtext VALUES(164,12,3,'',336,400,10);
INSERT INTO seqtext VALUES(164,12,1,'Je laisse Lilly  son stand et pars  la recherche d''Hanako.',1,0,0);
INSERT INTO seqtext VALUES(164,13,1,'D''une certaine manire, je me sens mal de la laisser avec toute cette foule, mais mme si elle tait clairement sous pression, je ne peux pas m''empcher de penser que a lui plaisait.',1,0,0);
INSERT INTO seqtext VALUES(164,14,2,'',222,0,0);
INSERT INTO seqtext VALUES(164,15,3,'',336,400,10);
INSERT INTO seqtext VALUES(164,15,1,'Les halls sont remplis de gens se baladant au festival.',1,0,0);
INSERT INTO seqtext VALUES(164,16,1,'S''il y a une chose que je sais  propos d''Hanako, c''est qu''elle ne s''approcherait pas de foules comme a.',1,0,0);
INSERT INTO seqtext VALUES(164,17,1,'Et avec les tudiants montrant leurs amis et leur chambre  leur famille, je doute qu''elle soit l aussi.',1,0,0);
INSERT INTO seqtext VALUES(164,18,1,'Suivant une intuition aveugle, je vais  l''encontre de la foule.',1,0,0);
INSERT INTO seqtext VALUES(164,19,1,'Heureusement, elle semble tre moins festive que dans un festival normal; je suppose que c''est par considration pour le corps tudiant.',1,0,0);
INSERT INTO seqtext VALUES(164,20,1,'Pendant que je me fraye un chemin  travers la masse, il ne faut pas longtemps pour que je me retrouve dans un endroit vide de monde.',1,0,0);
INSERT INTO seqtext VALUES(164,21,4,'crowd',0,0,0);
INSERT INTO seqtext VALUES(164,22,1,'Ce n''est pas surprenant, vu que je suis devant la bibliothque.',1,0,0);
INSERT INTO seqtext VALUES(164,23,1,'Mme les plus ardents tudiants n''ont pas pris la peine de montrer cette section de l''cole.',1,0,0);
INSERT INTO seqtext VALUES(164,24,2,'',165,0,0);
INSERT INTO seqtext VALUES(164,25,1,'Pendant que j''entre dans la bibliothque, le vacarme du festival se transforme en bruit de fond, et bientt je me retrouve dans la zone de lecture  l''arrire de la salle.',1,0,0);
INSERT INTO seqtext VALUES(164,26,1,'Derrire l''un des bureaux, je vois le haut d''une tte, avec des longs cheveux noirs.',1,0,0);
INSERT INTO seqtext VALUES(164,27,1,'Hey, Hanako. Je me doutais que j''allais te trouver ici...',12,0,0);
INSERT INTO seqtext VALUES(164,28,3,'hanako',187,400,10);
INSERT INTO seqtext VALUES(164,28,1,'Elle sursaute un peu avec le choc avant de me regarder lentement par dessus le bureau.',1,0,0);
INSERT INTO seqtext VALUES(164,29,1,'H-Hisao ?',13,0,0);
INSERT INTO seqtext VALUES(164,30,1,'Yep. Lilly est plutt occupe, alors elle m''a envoy te trouver.',12,0,0);
INSERT INTO seqtext VALUES(164,31,3,'hanako',106,400,10);
INSERT INTO seqtext VALUES(164,31,1,'O-oh. Tu veux t''asseoir ?',13,0,0);
INSERT INTO seqtext VALUES(164,32,1,'En fait, j''ai un peu faim. Tu veux qu''on aille chercher quelque chose  l''un des stands?',12,0,0);
INSERT INTO seqtext VALUES(164,33,3,'hanako',38,400,10);
INSERT INTO seqtext VALUES(164,33,1,'Um... Je... j''ai amen  manger alors...',13,0,0);
INSERT INTO seqtext VALUES(164,34,1,'J''aurais d m''en douter, mais j''aurais essay. Esprer d''elle qu''elle sorte aujourd''hui tait peut-tre un peu trop.',1,0,0);
INSERT INTO seqtext VALUES(164,35,1,'Que dirais-tu d''aller manger dans la salle de th ? Je suis pass devant en venant ici, et il n''y avait personne aux alentours.',12,0,0);
INSERT INTO seqtext VALUES(164,36,1,'On peut toujours manger ici, mais a serait un peu moins confortable. Qu''est-ce que t''en dis ?',12,0,0);
INSERT INTO seqtext VALUES(164,37,3,'hanako',108,400,10);
INSERT INTO seqtext VALUES(164,37,1,'D-d''accord. Allons-y.',13,0,0);
INSERT INTO seqtext VALUES(164,38,3,'hanako',424,400,10);
INSERT INTO seqtext VALUES(164,38,1,'Hanako ferme son livre et le pose d''une manire dlicate.',1,0,0);
INSERT INTO seqtext VALUES(164,39,1,'On y va ?',12,0,0);
INSERT INTO seqtext VALUES(164,40,3,'hanako',106,400,10);
INSERT INTO seqtext VALUES(164,40,1,'O... oui.',13,0,0);
INSERT INTO seqtext VALUES(164,41,2,'',222,0,0);
INSERT INTO seqtext VALUES(164,42,1,'Nous marchons cte  cte depuis la bibliothque jusqu''au salon de th.',1,0,0);
INSERT INTO seqtext VALUES(164,43,1,'Comme prvu, il n''y a quasiment personne ici.',1,0,0);
INSERT INTO seqtext VALUES(164,44,1,'S''il n''y avait pas ces murmures  travers les murs, il serait impossible de dire qu''il y a un norme festival  l''extrieur.',1,0,0);
INSERT INTO seqtext VALUES(164,45,3,'hanako',438,560,10);
INSERT INTO seqtext VALUES(164,45,1,'Hanako porte son sac avec ses deux mains et fixe le sol devant elle.',1,0,0);
INSERT INTO seqtext VALUES(164,46,3,'hanako',75,560,10);
INSERT INTO seqtext VALUES(164,46,3,'hanako',438,560,10);
INSERT INTO seqtext VALUES(164,46,1,'De temps en temps, elle semble briser son rythme et marche en faisant de plus petits pas.',1,0,0);
INSERT INTO seqtext VALUES(164,47,1,'La premire fois que c''est arriv, je n''y ai pas prt attention, mais je remarque vite qu''elle le fait rgulirement.',1,0,0);
INSERT INTO seqtext VALUES(164,48,1,'a va ?',12,0,0);
INSERT INTO seqtext VALUES(164,49,3,'hanako',38,560,10);
INSERT INTO seqtext VALUES(164,49,1,'Elle s''arrte net.',1,0,0);
INSERT INTO seqtext VALUES(164,50,1,'Q-quoi ?',13,0,0);
INSERT INTO seqtext VALUES(164,51,1,'Je sais pas... on dirait que tu tais en train de trbucher ou un truc comme a...',12,0,0);
INSERT INTO seqtext VALUES(164,52,3,'hanako',146,560,10);
INSERT INTO seqtext VALUES(164,52,1,'Ses joues se teintent de rose alors que son regard se reconcentre sur le sol.',1,0,0);
INSERT INTO seqtext VALUES(164,53,3,'hanako',438,560,10);
INSERT INTO seqtext VALUES(164,53,1,'C''est... ce n''est rien.',13,0,0);
INSERT INTO seqtext VALUES(164,54,1,'Tu sais, quand tu dis rien comme a, les gens ont envie de continuer  poser des questions.',12,0,0);
INSERT INTO seqtext VALUES(164,55,1,'Pendant une seconde, j''ai cru qu''elle n''allait pas rpondre.',1,0,0);
INSERT INTO seqtext VALUES(164,56,1,'Prt  repartir, je recommence presque  marcher, quand...',1,0,0);
INSERT INTO seqtext VALUES(164,57,3,'hanako',526,560,10);
INSERT INTO seqtext VALUES(164,57,1,'C''est un... un jeu.',13,0,0);
INSERT INTO seqtext VALUES(164,58,1,'Un jeu ?',12,0,0);
INSERT INTO seqtext VALUES(164,59,3,'hanako',75,560,10);
INSERT INTO seqtext VALUES(164,59,1,'Tu... vois le sol l ?',13,0,0);
INSERT INTO seqtext VALUES(164,60,1,'Quelle question bizarre. Le sol ressemble  celui de n''importe quel autre tage; couvert de planches faites avec des carrs de linolum.',1,0,0);
INSERT INTO seqtext VALUES(164,61,1,'Rien de spcial.',1,0,0);
INSERT INTO seqtext VALUES(164,62,1,'Ben, oui. Et donc ?',12,0,0);
INSERT INTO seqtext VALUES(164,63,3,'hanako',438,560,10);
INSERT INTO seqtext VALUES(164,63,1,'Des fois... quand il n''y a personne... je ne marche que sur les planches fonces...',13,0,0);
INSERT INTO seqtext VALUES(164,64,1,'La voix d''Hanako s''affaisse au fur et  mesure que son explication continue, jusqu''au stade o je peux  peine entendre sa voix par dessus le silence hurlant de la salle vide.',1,0,0);
INSERT INTO seqtext VALUES(164,65,1,'Les plus fonces ?',12,0,0);
INSERT INTO seqtext VALUES(164,66,1,'Remuant les pieds, Hanako pointe du bout de sa chaussure une latte qui est  peine plus fonce que les autres.',1,0,0);
INSERT INTO seqtext VALUES(164,67,3,'hanako',75,560,10);
INSERT INTO seqtext VALUES(164,67,1,'C-comme celles-l.',13,0,0);
INSERT INTO seqtext VALUES(164,68,1,'Oh, d''accord, donc celles-l ne sont pas bonnes ?',12,0,0);
INSERT INTO seqtext VALUES(164,69,1,'Je pointe une planche  proximit.',1,0,0);
INSERT INTO seqtext VALUES(164,70,3,'hanako',526,560,10);
INSERT INTO seqtext VALUES(164,70,1,'O-oui. Quelque chose... quelque chose comme a.',13,0,0);
INSERT INTO seqtext VALUES(164,71,1,'Oh, je vois. Tu joues souvent  ce jeu ?',12,0,0);
INSERT INTO seqtext VALUES(164,72,3,'hanako',185,560,10);
INSERT INTO seqtext VALUES(164,72,1,'Hanako secoue la tte.',1,0,0);
INSERT INTO seqtext VALUES(164,73,1,'Juste quand le couloir est vide ?',12,0,0);
INSERT INTO seqtext VALUES(164,74,3,'hanako',526,560,10);
INSERT INTO seqtext VALUES(164,74,1,'Elle hoche la tte.',1,0,0);
INSERT INTO seqtext VALUES(164,75,1,'Bon eh bien, inutile de s''arrter, je commence  avoir vraiment faim.',12,0,0);
INSERT INTO seqtext VALUES(164,76,3,'hanako',199,560,10);
INSERT INTO seqtext VALUES(164,76,1,'Elle hoche la tte encore une fois, cette fois avec un peu plus d''enthousiasme.',1,0,0);
INSERT INTO seqtext VALUES(164,77,1,'Bien, allons-y.',12,0,0);
INSERT INTO seqtext VALUES(164,78,4,'hanako',0,0,0);
INSERT INTO seqtext VALUES(164,79,1,'Nous arrivons au bout du couloir, et cette fois je remarque qu''Hanako porte un peu moins d''attention au sol.',1,0,0);
INSERT INTO seqtext VALUES(164,80,1,'Je me demande;  quel point est-ce qu''il faut se sentir seul pour jouer  un jeu comme a ?',1,0,0);
INSERT INTO seqtext VALUES(164,81,1,'Mais, avant que je ralise ce que je suis en train de faire, je me surprends  essayer de viser chaque pas pour marcher sur les bonnes planches.',1,0,0);
INSERT INTO seqtext VALUES(164,82,2,'',179,0,0);
INSERT INTO seqtext VALUES(164,83,1,'Le bruit du festival est un peu plus fort dans la salle de th, mais avec la brise qui vient de la fentre, a en vaut la peine.',1,0,0);
INSERT INTO seqtext VALUES(164,84,1,'Sans rflchir, je marche jusqu'' la fentre et respire profondment. J''oublie parfois  quel point l''air est pur ici compar  chez moi.',1,0,0);
INSERT INTO seqtext VALUES(164,85,3,'hanako',358,400,10);
INSERT INTO seqtext VALUES(164,85,1,'Je... te sers du th ?',13,0,0);
INSERT INTO seqtext VALUES(164,86,1,'Je veux bien, merci.',12,0,0);
INSERT INTO seqtext VALUES(164,87,4,'hanako',0,0,0);
INSERT INTO seqtext VALUES(164,88,1,'Il me semble que c''est la premire fois que je suis seul avec Hanako sans qu''elle veuille tre ailleurs.',1,0,0);
INSERT INTO seqtext VALUES(164,89,1,'Me tournant, je la regarde faire un simple pot de th et organiser quelques sandwichs sur un plat.',1,0,0);
INSERT INTO seqtext VALUES(164,90,1,'Je l''ai vue faire a plusieurs fois auparavant, mais cette fois semble lgrement diffrente.',1,0,0);
INSERT INTO seqtext VALUES(164,91,1,'C''est comme si elle tait...{w} calme.',1,0,0);
INSERT INTO seqtext VALUES(164,92,1,'Finalement, elle place le petit plateau sur la table et remplit les tasses de th.',1,0,0);
INSERT INTO seqtext VALUES(164,93,1,'Le parfum frais du th infus est ml  la brise, et pendant une seconde je me sens comme si j''tais seul au monde.',1,0,0);
INSERT INTO seqtext VALUES(164,94,1,'Je crois que maintenant je sais pourquoi tu aimes cet endroit.',12,0,0);
INSERT INTO seqtext VALUES(164,95,3,'hanako',38,400,10);
INSERT INTO seqtext VALUES(164,95,1,'Um... Je ne comprends pas ce que tu veux dire.',13,0,0);
INSERT INTO seqtext VALUES(164,96,1,'Eh bien, y a pas mal de gens dehors, mais ici c''est comme un autre monde.',12,0,0);
INSERT INTO seqtext VALUES(164,97,1,'Tu pourrais prtendre qu''il n''y a personne  des kilomtres  la ronde.',12,0,0);
INSERT INTO seqtext VALUES(164,98,3,'hanako',438,400,10);
INSERT INTO seqtext VALUES(164,98,1,'C-c''est vrai.',13,0,0);
INSERT INTO seqtext VALUES(164,99,1,'C''est comme si le monde avait oubli cette pice.',13,0,0);
INSERT INTO seqtext VALUES(164,100,3,'hanako',526,400,10);
INSERT INTO seqtext VALUES(164,100,1,'Et a-avec a, tu peux oublier le monde extrieur.',13,0,0);
INSERT INTO seqtext VALUES(164,101,1,'a doit tre attirant dans certains cas.',1,0,0);
INSERT INTO seqtext VALUES(164,102,1,'Pour autant que je sache, il n''y pas d''lves qui en martyrisent d''autres dans cette cole.',1,0,0);
INSERT INTO seqtext VALUES(164,103,1,'Mais l encore, je n''ai pas vu une seule personne parler  Hanako mise  part Lilly.',1,0,0);
INSERT INTO seqtext VALUES(164,104,1,'Si on est ignor par le monde, un endroit o on peut oublier son existence doit tre vraiment attirant.',1,0,0);
INSERT INTO seqtext VALUES(164,105,1,'C''est bien. J''aime les pices qui donnent cette impression de totale libert.',12,0,0);
INSERT INTO seqtext VALUES(164,106,3,'hanako',199,400,10);
INSERT INTO seqtext VALUES(164,106,1,'O-ouais.',13,0,0);
INSERT INTO seqtext VALUES(164,107,3,'hanako',358,400,10);
INSERT INTO seqtext VALUES(164,107,1,'Dis... tu joues aux checs ?',13,0,0);
INSERT INTO seqtext VALUES(164,108,1,'Les checs ? Un peu, j''imagine.',12,0,0);
INSERT INTO seqtext VALUES(164,109,1,'J''en dduis que t''y joues ?',12,0,0);
INSERT INTO seqtext VALUES(164,110,3,'hanako',424,400,10);
INSERT INTO seqtext VALUES(164,110,1,'Un peu...',13,0,0);
INSERT INTO seqtext VALUES(164,111,4,'hanako',0,0,0);
INSERT INTO seqtext VALUES(164,112,1,'Sans dire un mot de plus, Hanako va vers l''une des armoires et en sort un petit jeu d''checs.',1,0,0);
INSERT INTO seqtext VALUES(164,113,1,'Tu... tu veux...',13,0,0);
INSERT INTO seqtext VALUES(164,114,1,'Bien sr, pourquoi pas ?',12,0,0);
INSERT INTO seqtext VALUES(164,115,1,'Je lui coupe la parole, mais elle ne semble pas y faire attention.',1,0,0);
INSERT INTO seqtext VALUES(164,116,2,'',179,0,0);
INSERT INTO seqtext VALUES(164,117,3,'hanako',428,400,10);
INSERT INTO seqtext VALUES(164,117,1,'On met les pices en place, et il ne faut pas longtemps pour que nous avancions les pions pour une charge vers leur destin invitable.',1,0,0);
INSERT INTO seqtext VALUES(164,118,1,'Je prends mon temps et examine attentivement chaque mouvement et ses consquences, la nostalgie pour le jeu prend une place secondaire devant ma concentration.',1,0,0);
INSERT INTO seqtext VALUES(164,119,1,'Pendant un moment le jeu reste sur une longue bataille d''usure, mais je remarque une ouverture et perce sa dfense.',1,0,0);
INSERT INTO seqtext VALUES(164,120,3,'hanako',161,400,10);
INSERT INTO seqtext VALUES(164,120,1,'Quelques mouvements plus tard, son roi est entour par plusieurs de mes pices.',1,0,0);
INSERT INTO seqtext VALUES(164,121,1,'chec et mat.',12,0,0);
INSERT INTO seqtext VALUES(164,122,1,'T''es pas mauvaise  a, hein ?',12,0,0);
INSERT INTO seqtext VALUES(164,123,1,'Une valuation honnte. Sa technique est plutt bonne, mais plusieurs fois j''ai t en mesure d''exploiter son manque de prvision.',1,0,0);
INSERT INTO seqtext VALUES(164,124,1,'Je ramasse une pice et l''examine. Elle semble relativement nouvelle, mais use pour son ge.',1,0,0);
INSERT INTO seqtext VALUES(164,125,3,'hanako',130,400,10);
INSERT INTO seqtext VALUES(164,125,1,'Je... j''imagine que non.',13,0,0);
INSERT INTO seqtext VALUES(164,126,1,'Lilly y joue ?',12,0,0);
INSERT INTO seqtext VALUES(164,127,3,'hanako',341,400,10);
INSERT INTO seqtext VALUES(164,127,1,'L''absence de rponse de la part d''Hanako me fait reconsidrer ma question.',1,0,0);
INSERT INTO seqtext VALUES(164,128,1,'Un... Un peu...',13,0,0);
INSERT INTO seqtext VALUES(164,129,1,'C-c''est la premire fois que je joue contre quelqu''un... d''autre qu''elle, ou...',13,0,0);
INSERT INTO seqtext VALUES(164,130,3,'hanako',412,400,10);
INSERT INTO seqtext VALUES(164,130,1,'Ou... ?',1,0,0);
INSERT INTO seqtext VALUES(164,131,1,'Elle s''arrte brusquement, laissant la rponse suspendue en l''air. Quelqu''un qu''elle connaissait avant de venir  Yamaku, peut-tre.',1,0,0);
INSERT INTO seqtext VALUES(164,132,1,'Eh bien, je suis honor d''avoir jou contre toi.',12,0,0);
INSERT INTO seqtext VALUES(164,133,3,'hanako',445,400,10);
