INSERT INTO seqtext VALUES(1,3,1,'Une légère brise faisait tinter les branches dégarnies comme des carillons en bois.',1,0,0);
INSERT INTO seqtext VALUES(1,4,1,'Il s''agissait d''un endroit populaire pour les couples pendant l''été. Les arbres feuillus permettaient l''existence d''un superbe auvent vert, loin de la vue des professeurs et des étudiants.',1,0,0);
INSERT INTO seqtext VALUES(1,5,1,'Mais à ce moment-là, en fin d''hiver, c''était comme si j''étais debout sous une pile de petit bois.',1,0,0);
INSERT INTO seqtext VALUES(1,6,1,'Je soufflais sur mes mains et les frottais nerveusement ensemble pour les empêcher de s''engourdir dans ce froid.',1,0,0);
INSERT INTO seqtext VALUES(1,7,1,'Combien de temps au juste étais-je censé attendre ici ? J''étais sûr que le message disait 16 heures.',12,0,0);
INSERT INTO seqtext VALUES(1,8,1,'Ah oui... le message... glissé entre les pages de mon livre de maths alors que je ne regardais pas.',1,0,0);
INSERT INTO seqtext VALUES(1,9,1,'Aussi loin que pouvaient aller les clichés, je préférais la lettre dans le casier, mais au moins, cette façon de faire montre qu''elle avait un peu d''initiative.',1,0,0);
INSERT INTO seqtext VALUES(1,10,1,'Pendant que je méditais sur le sens de la note, les chutes de neige s''épaississaient.',1,0,0);
INSERT INTO seqtext VALUES(1,11,1,'Les flocons de neige tombant silencieusement du ciel étaient le seul signe du temps qui passait dans ce monde stagnant.',1,0,0);
INSERT INTO seqtext VALUES(1,12,1,'Leur lente descente sur la forêt gelée faisait penser que le temps s''était ralenti.',1,0,0);
INSERT INTO seqtext VALUES(1,13,1,'Le bruissement des pas sur la neige me fit sursauter, interrompant ce moment de calme. Quelqu''un approchait par derrière.',1,0,0);
INSERT INTO seqtext VALUES(1,14,1,'Salut... Hisao ? Tu es venu ?',3,0,0);
INSERT INTO seqtext VALUES(1,15,1,'Une question hésitante, presque inaudible.',1,0,0);
INSERT INTO seqtext VALUES(1,16,1,'Cependant, je reconnus la propriétaire de cette délicate voix instantanément.',1,0,0);
INSERT INTO seqtext VALUES(1,17,1,'Je sentis mon cœur rater un battement.',1,0,0);
INSERT INTO seqtext VALUES(1,18,1,'C''est une voix que j''avais entendue des centaines de fois, mais jamais d''une autre façon qu''en prêtant une oreille indiscrète lors d''une conversation.',1,0,0);
INSERT INTO seqtext VALUES(1,19,1,'Je me retournai pour faire face à cette voix, la voix de mes rêves, et mon cœur commença à s''emballer.',1,0,0);
INSERT INTO seqtext VALUES(1,20,2,'',43,0,0);
INSERT INTO seqtext VALUES(1,21,2,'',235,0,0);
INSERT INTO seqtext VALUES(1,22,1,'Iwanako ? J''ai reçu un message me disant d''attendre ici... c''était le tien ?',1,0,0);
INSERT INTO seqtext VALUES(1,23,1,'Fait chier. J''avais passé tout l''après-midi à essayer de trouver une bonne réplique, et voilà le résultat.',1,0,0);
INSERT INTO seqtext VALUES(1,24,1,'Pathétique.',1,0,0);
INSERT INTO seqtext VALUES(1,25,1,'Ah... oui. J''ai demandé à un ami de te donner ce message... Je suis heureuse que tu l''aies reçu.',4,0,0);
INSERT INTO seqtext VALUES(1,26,1,'Son timide mais joyeux sourire me rendait si tendu que je ne pouvais pas bouger un seul muscle, même si j''essayais.',1,0,0);
INSERT INTO seqtext VALUES(1,27,2,'',43,0,0);
INSERT INTO seqtext VALUES(1,28,2,'',566,0,0);
