INSERT INTO seqtext VALUES(1,30,3,'heartattack',396,400,10);
INSERT INTO seqtext VALUES(1,30,4,'heartattack_alpha',0,0,0);
INSERT INTO seqtext VALUES(1,31,1,'Mon cœur battait la chamade à cet instant, comme s''il cherchait à jaillir de ma poitrine et réclamer cette fille pour lui seul.',1,0,0);
INSERT INTO seqtext VALUES(1,32,2,'',43,0,0);
INSERT INTO seqtext VALUES(1,33,2,'',457,0,0);
INSERT INTO seqtext VALUES(1,34,1,'Donc... euh... nous y sommes. Dehors dans le froid...',12,0,0);
INSERT INTO seqtext VALUES(1,35,1,'Une fois encore, le vent agitait les branches. Le bruit de cette cacophonie était une musique à mes oreilles.',1,0,0);
INSERT INTO seqtext VALUES(1,36,1,'Iwanako frissonna légèrement à cause des rafales de vent.',1,0,0);
INSERT INTO seqtext VALUES(1,37,1,'À ce moment, elle devint sûre d''elle-même, comme si elle avait été soutenue par une sorte de nouvelle confiance.',1,0,0);
INSERT INTO seqtext VALUES(1,38,1,'Ses yeux étaient rivés sur les miens et elle enroulait nonchalamment ses longs cheveux sombres autour de son doigt.',1,0,0);
INSERT INTO seqtext VALUES(1,39,1,'Pendant tout ce temps, l''anxiété faisait battre mon cœur plus fort.',1,0,0);
INSERT INTO seqtext VALUES(1,40,2,'',43,0,0);
INSERT INTO seqtext VALUES(1,41,2,'',566,0,0);
INSERT INTO seqtext VALUES(1,42,1,'Ma gorge était serrée; je doutais de pouvoir réussir à sortir un mot, même si je me forçais.',1,0,0);
INSERT INTO seqtext VALUES(1,43,1,'Tu vois...',4,0,0);
INSERT INTO seqtext VALUES(1,44,3,'heartattack',396,400,10);
INSERT INTO seqtext VALUES(1,44,4,'heartattack_alpha',0,0,0);
INSERT INTO seqtext VALUES(1,45,1,'...Je voudrais savoir...',4,0,0);
INSERT INTO seqtext VALUES(1,46,3,'heartattack',396,400,10);
INSERT INTO seqtext VALUES(1,46,4,'heartattack_alpha',0,0,0);
INSERT INTO seqtext VALUES(1,47,3,'heartattack',396,400,10);
INSERT INTO seqtext VALUES(1,47,4,'heartattack_alpha',0,0,0);
INSERT INTO seqtext VALUES(1,48,1,'...si tu veux bien sortir avec moi...',4,0,0);
INSERT INTO seqtext VALUES(1,49,1,'Je restais là, immobile, sauf pour mon cœur battant.',1,0,0);
INSERT INTO seqtext VALUES(1,50,1,'Je voulais dire quelque chose en réponse, mais mes cordes vocales se trouvaient comme au-delà du point de rupture.',1,0,0);
INSERT INTO seqtext VALUES(1,51,1,'...Hisao ?',4,0,0);
INSERT INTO seqtext VALUES(1,52,1,'Je levais la main pour essayer de me masser la gorge, mais cela ne fit qu''envoyer des pointes de douleur aveuglante le long de mon bras.',1,0,0);
INSERT INTO seqtext VALUES(1,53,1,'Hisao ?!',4,0,0);
INSERT INTO seqtext VALUES(1,54,3,'heartattack',396,400,10);
INSERT INTO seqtext VALUES(1,54,4,'heartattack_alpha',0,0,0);
INSERT INTO seqtext VALUES(1,55,1,'Mon corps entier se figea, sauf mes yeux, qui restèrent ouverts sur le coup de la terreur.',1,0,0);
INSERT INTO seqtext VALUES(1,56,3,'heartattack',396,400,10);
INSERT INTO seqtext VALUES(1,56,4,'heartattack_alpha',0,0,0);
